     */
    public function testApply()
    {
        $qb = $this->getQueryBuilder();
        $qb->select('b')
            ->from('Entity', 'b');
        $this->object->setQueryBuilder($qb);
        $this->object->apply(array('value' => 'true'), 'boolean');

        $this->assertEquals('SELECT b FROM Entity b WHERE b.boolean = true', $qb->getDQL());
    }

